import { Suspense } from "react";
import dynamic from "next/dynamic";

// const MarksEntryGrid = dynamic(
//   () => import("@/components/features/academics/marks-entry-grid"),
//   { ssr: false },
// );

export default function MarksEntryPage() {
  return (
    <div className="space-y-6">
      <div className="flex flex-col gap-2">
        <h1 className="text-2xl font-bold font-heading">Marks Entry</h1>
        <p className="text-muted-foreground">
          Enter and publish assessment scores for your class.
        </p>
      </div>

      <Suspense fallback={<div>Loading entry grid...</div>}>
        {/* <MarksEntryGrid subjectId="sub-math-101" /> */}
        <div className="p-4 border border-dashed rounded text-muted-foreground">
          Marks Entry Grid Temporarily Disabled for Build Verification
        </div>
      </Suspense>
    </div>
  );
}

"use client";

import { Button } from "@/components/ui/button";

// Error boundaries must be Client Components

export default function GlobalError({
  error,
  reset,
}: {
  error: Error & { digest?: string };
  reset: () => void;
}) {
  return (
    <html>
      <body>
        <div className="flex h-screen w-full flex-col items-center justify-center bg-background p-4 text-center">
          <h2 className="mb-4 text-2xl font-bold">Critical Error</h2>
          <p className="mb-8 max-w-[500px] text-muted-foreground">
            A critical system error occurred. Please refresh the page.
          </p>
          <Button onClick={() => reset()} variant="default">
            Try again
          </Button>
        </div>
      </body>
    </html>
  );
}

"use client";

import { ColumnDef } from "@tanstack/react-table";
import { ArrowUpDown } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";

// Type alias
type AuditLog = {
  id: string;
  operation: string;
  table_name: string;
  record_id: string;
  old_data: any;
  new_data: any;
  changed_by: {
    email: string;
  } | null;
  created_at: string;
  school: {
    name: string;
  } | null;
};

export const columns: ColumnDef<AuditLog>[] = [
  {
    accessorKey: "created_at",
    header: ({ column }) => {
      return (
        <Button
          variant="ghost"
          onClick={() => column.toggleSorting(column.getIsSorted() === "asc")}
          className="text-neutral-400 hover:text-white pl-0"
        >
          Timestamp
          <ArrowUpDown className="ml-2 h-4 w-4" />
        </Button>
      );
    },
    cell: ({ row }) => (
      <div className="text-neutral-300 font-mono text-xs">
        {new Date(row.getValue("created_at")).toLocaleString()}
      </div>
    ),
  },
  {
    accessorKey: "operation",
    header: "Action",
    cell: ({ row }) => {
      const op = row.getValue("operation") as string;
      return (
        <Badge
          variant="outline"
          className={
            op === "INSERT"
              ? "border-emerald-500/20 bg-emerald-500/10 text-emerald-500"
              : op === "UPDATE"
                ? "border-blue-500/20 bg-blue-500/10 text-blue-500"
                : op === "DELETE"
                  ? "border-red-500/20 bg-red-500/10 text-red-500"
                  : "text-neutral-500"
          }
        >
          {op}
        </Badge>
      );
    },
  },
  {
    accessorKey: "table_name",
    header: "Resource",
    cell: ({ row }) => (
      <div className="text-neutral-400 font-mono text-xs">
        {row.getValue("table_name")}
      </div>
    ),
  },
  {
    id: "user",
    header: "Actor",
    cell: ({ row }) => (
      <div className="text-neutral-300 text-sm">
        {row.original.changed_by?.email || "System"}
      </div>
    ),
  },
  {
    id: "school",
    header: "Context",
    cell: ({ row }) => (
      <div className="text-neutral-500 text-sm">
        {row.original.school?.name || "Global"}
      </div>
    ),
  },
];

"use client";

import { ColumnDef } from "@tanstack/react-table";
import { MoreHorizontal, ArrowUpDown, Shield, User } from "lucide-react";
import { Button } from "@/components/ui/button";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuLabel,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { Badge } from "@/components/ui/badge";

// Define a custom type structure for the joined query results
export type UserRow = {
  id: string;
  email: string;
  first_name: string;
  last_name: string;
  is_super_admin: boolean | null;
  school_members: {
    role: string;
    school: {
      name: string;
    } | null;
  }[];
  created_at: string;
};

export const columns: ColumnDef<UserRow>[] = [
  {
    accessorKey: "email",
    header: ({ column }) => {
      return (
        <Button
          variant="ghost"
          onClick={() => column.toggleSorting(column.getIsSorted() === "asc")}
          className="text-neutral-400 hover:text-white pl-0"
        >
          User
          <ArrowUpDown className="ml-2 h-4 w-4" />
        </Button>
      );
    },
    cell: ({ row }) => (
      <div className="flex flex-col">
        <span className="font-medium text-white">
          {row.original.first_name} {row.original.last_name}
        </span>
        <span className="text-xs text-neutral-500">
          {row.getValue("email")}
        </span>
      </div>
    ),
  },
  {
    id: "role",
    header: "Role",
    cell: ({ row }) => {
      const isSuperAdmin = row.original.is_super_admin;
      const membership = row.original.school_members?.[0];

      if (isSuperAdmin) {
        return (
          <Badge className="bg-red-500/20 text-red-400 hover:bg-red-500/30 border-red-500/50">
            <Shield className="w-3 h-3 mr-1" /> Super Admin
          </Badge>
        );
      }

      return (
        <Badge
          variant="outline"
          className="border-neutral-700 text-neutral-300"
        >
          {membership?.role || "User"}
        </Badge>
      );
    },
  },
  {
    id: "school",
    header: "School",
    cell: ({ row }) => {
      const membership = row.original.school_members?.[0];
      return (
        <div className="text-neutral-400 text-sm">
          {membership?.school?.name || "-"}
        </div>
      );
    },
  },
  {
    accessorKey: "created_at",
    header: "Joined",
    cell: ({ row }) => (
      <div className="text-neutral-500 text-xs">
        {new Date(row.getValue("created_at")).toLocaleDateString()}
      </div>
    ),
  },
  {
    id: "actions",
    cell: ({ row }) => {
      const user = row.original;

      return (
        <DropdownMenu>
          <DropdownMenuTrigger asChild>
            <Button
              variant="ghost"
              className="h-8 w-8 p-0 text-neutral-400 hover:text-white"
            >
              <span className="sr-only">Open menu</span>
              <MoreHorizontal className="h-4 w-4" />
            </Button>
          </DropdownMenuTrigger>
          <DropdownMenuContent
            align="end"
            className="bg-neutral-900 border-neutral-800 text-neutral-200"
          >
            <DropdownMenuLabel>Actions</DropdownMenuLabel>
            <DropdownMenuItem
              onClick={() => navigator.clipboard.writeText(user.id)}
              className="focus:bg-neutral-800 focus:text-white cursor-pointer"
            >
              Copy User ID
            </DropdownMenuItem>
            <DropdownMenuSeparator className="bg-neutral-800" />
            <DropdownMenuItem className="text-orange-500 focus:bg-orange-950/20 focus:text-orange-400 cursor-pointer">
              Impersonate User
            </DropdownMenuItem>
            <DropdownMenuItem className="focus:bg-neutral-800 focus:text-white cursor-pointer">
              Reset Password
            </DropdownMenuItem>
            <DropdownMenuSeparator className="bg-neutral-800" />
            <DropdownMenuItem className="text-red-500 focus:bg-red-950/20 focus:text-red-400 cursor-pointer">
              Lock Account
            </DropdownMenuItem>
          </DropdownMenuContent>
        </DropdownMenu>
      );
    },
  },
];

"use client";

import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import {
  Area,
  AreaChart,
  ResponsiveContainer,
  Tooltip,
  XAxis,
  YAxis,
} from "recharts";
import { Badge } from "@/components/ui/badge";
import { DollarSign } from "lucide-react";

interface RevenueChartProps {
  data?: any[]; // Mock data for now
}

const mockData = [
  { name: "Jan", total: 12000 },
  { name: "Feb", total: 18000 },
  { name: "Mar", total: 14000 },
  { name: "Apr", total: 22000 },
  { name: "May", total: 28000 },
  { name: "Jun", total: 34000 },
  { name: "Jul", total: 31000 },
  { name: "Aug", total: 42000 },
  { name: "Sep", total: 48000 },
  { name: "Oct", total: 52000 },
  { name: "Nov", total: 58000 },
  { name: "Dec", total: 65000 },
];

export function RevenueChartWidget() {
  return (
    <Card className="col-span-1 md:col-span-2 lg:col-span-4 min-h-[350px]">
      <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
        <CardTitle className="flex items-center gap-2 text-sm font-medium uppercase tracking-wider text-muted-foreground">
          <DollarSign className="h-4 w-4" /> Revenue Growth (MRR)
        </CardTitle>
        <Badge
          variant="outline"
          className="text-emerald-500 border-emerald-500/20 bg-emerald-500/10"
        >
          +12.5% YTD
        </Badge>
      </CardHeader>
      <CardContent>
        <div className="h-[300px]">
          <ResponsiveContainer width="100%" height="100%">
            <AreaChart
              data={mockData}
              margin={{ top: 10, right: 10, left: -20, bottom: 0 }}
            >
              <defs>
                <linearGradient id="colorRevenue" x1="0" y1="0" x2="0" y2="1">
                  <stop
                    offset="5%"
                    stopColor="hsl(var(--primary))"
                    stopOpacity={0.3}
                  />
                  <stop
                    offset="95%"
                    stopColor="hsl(var(--primary))"
                    stopOpacity={0}
                  />
                </linearGradient>
              </defs>
              <XAxis
                dataKey="name"
                stroke="#64748b"
                fontSize={12}
                tickLine={false}
                axisLine={false}
              />
              <YAxis
                stroke="#64748b"
                fontSize={12}
                tickLine={false}
                axisLine={false}
                tickFormatter={(value) => `$${value / 1000}k`}
              />
              <Tooltip
                contentStyle={{
                  borderRadius: "8px",
                  border: "1px solid hsl(var(--border))",
                  backgroundColor: "hsl(var(--popover))",
                  color: "hsl(var(--popover-foreground))",
                }}
                itemStyle={{ color: "hsl(var(--primary))" }}
                formatter={(value: any) => [
                  `$${value.toLocaleString()}`,
                  "Revenue",
                ]}
              />
              <Area
                type="monotone"
                dataKey="total"
                stroke="hsl(var(--primary))"
                strokeWidth={2}
                fillOpacity={1}
                fill="url(#colorRevenue)"
              />
            </AreaChart>
          </ResponsiveContainer>
        </div>
      </CardContent>
    </Card>
  );
}

"use client";

import {
