"use client";

import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { ArrowDown, ArrowUp, Minus, TrendingUp } from "lucide-react";
import { KPIMetric } from "@/services/analytics/schemas";
import { AnalyticsService } from "@/services/analytics/mock-service";
import { useQuery } from "@tanstack/react-query";
import { Skeleton } from "@/components/ui/skeleton";

export function KPIGrid() {
  const { data: metrics, isLoading } = useQuery({
    queryKey: ["analytics-kpi"],
    queryFn: AnalyticsService.getOverviewMetrics,
  });

  if (isLoading) {
    return (
      <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-4">
        {[1, 2, 3, 4].map((i) => (
          <Skeleton key={i} className="h-32 w-full" />
        ))}
      </div>
    );
  }

  return (
    <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-4">
      {metrics?.map((metric) => (
        <Card key={metric.id}>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">
              {metric.label}
            </CardTitle>
            <TrendingUp className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{metric.value}</div>
            <div className="flex items-center text-xs text-muted-foreground mt-1">
              {metric.trend === "up" ? (
                <ArrowUp className="mr-1 h-3 w-3 text-green-500" />
              ) : metric.trend === "down" ? (
                <ArrowDown className="mr-1 h-3 w-3 text-red-500" />
              ) : (
                <Minus className="mr-1 h-3 w-3" />
              )}
              <span
                className={
                  metric.status === "positive"
                    ? "text-green-500 font-medium"
                    : metric.status === "negative"
                      ? "text-red-500 font-medium"
                      : ""
                }
              >
                {Math.abs(metric.change)}%
              </span>
              <span className="ml-1">from last term</span>
            </div>
          </CardContent>
        </Card>
      ))}
    </div>
  );
}

"use client";

import {
  Card,
  CardContent,
  CardHeader,
  CardTitle,
  CardDescription,
} from "@/components/ui/card";
import {
  Radar,
  RadarChart,
  PolarGrid,
  PolarAngleAxis,
  PolarRadiusAxis,
  ResponsiveContainer,
  Legend,
} from "recharts";
import { Skeleton } from "@/components/ui/skeleton";

interface PerformanceRadarProps {
  data?: any[];
  isLoading?: boolean;
}

export function PerformanceRadar({ data, isLoading }: PerformanceRadarProps) {
  if (isLoading) return <Skeleton className="h-[400px] w-full" />;

  const mockData = data || [
    { subject: "Math", student: 120, average: 110, fullMark: 150 },
    { subject: "Chinese", student: 98, average: 130, fullMark: 150 },
    { subject: "English", student: 86, average: 130, fullMark: 150 },
    { subject: "Geography", student: 99, average: 100, fullMark: 150 },
    { subject: "Physics", student: 85, average: 90, fullMark: 150 },
    { subject: "History", student: 65, average: 85, fullMark: 150 },
  ];

  return (
    <Card className="col-span-full lg:col-span-3">
      <CardHeader>
        <CardTitle>Subject Mastery</CardTitle>
        <CardDescription>Comparing student vs class average</CardDescription>
      </CardHeader>
      <CardContent>
        <div className="h-[300px] w-full">
          <ResponsiveContainer width="100%" height="100%">
            <RadarChart cx="50%" cy="50%" outerRadius="80%" data={mockData}>
              <PolarGrid stroke="#e5e7eb" />
              <PolarAngleAxis
                dataKey="subject"
                tick={{ fill: "#6b7280", fontSize: 12 }}
              />
              <PolarRadiusAxis
                angle={30}
                domain={[0, 150]}
                tick={false}
                axisLine={false}
              />
              <Radar
                name="Student"
                dataKey="student"
                stroke="#ea580c"
                fill="#ea580c"
                fillOpacity={0.5}
              />
              <Radar
                name="Class Avg"
                dataKey="average"
                stroke="#94a3b8"
                fill="#94a3b8"
                fillOpacity={0.3}
              />
              <Legend />
            </RadarChart>
          </ResponsiveContainer>
        </div>
      </CardContent>
    </Card>
  );
}

"use client";

import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { MoreHorizontal, AlertTriangle, ArrowRight } from "lucide-react";
import { AnalyticsService } from "@/services/analytics/mock-service";
import { useQuery } from "@tanstack/react-query";
import { Skeleton } from "@/components/ui/skeleton";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuLabel,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";

export function RiskTable() {
  const { data: students, isLoading } = useQuery({
    queryKey: ["risk-students"],
    queryFn: AnalyticsService.getRiskAnalysis,
  });

  if (isLoading) return <Skeleton className="h-[500px] w-full" />;

  return (
    <div className="rounded-md border bg-white dark:bg-slate-950">
      <Table>
        <TableHeader>
          <TableRow>
            <TableHead>Student</TableHead>
            <TableHead>Class</TableHead>
            <TableHead>Risk Score</TableHead>
            <TableHead>Risk Factors</TableHead>
            <TableHead>Trend</TableHead>
            <TableHead className="text-right">Actions</TableHead>
          </TableRow>
        </TableHeader>
        <TableBody>
          {students?.map((student) => (
            <TableRow key={student.studentId}>
              <TableCell className="font-medium">
                <div className="flex flex-col">
                  <span>{student.name}</span>
                  <span className="text-xs text-muted-foreground">
                    ID: {student.studentId}
                  </span>
                </div>
              </TableCell>
              <TableCell>{student.class}</TableCell>
              <TableCell>
                <div className="flex items-center gap-2">
                  <span
                    className={`font-bold ${
                      student.riskLevel === "high"
                        ? "text-red-600"
                        : student.riskLevel === "medium"
                          ? "text-orange-600"
                          : "text-yellow-600"
                    }`}
                  >
                    {student.riskScore}
                  </span>
                  {student.riskLevel === "high" && (
                    <AlertTriangle className="h-4 w-4 text-red-500" />
                  )}
                </div>
              </TableCell>
              <TableCell>
                <div className="flex flex-wrap gap-1">
                  {student.factors.map((factor, i) => (
                    <Badge
                      key={i}
                      variant="outline"
                      className="text-[10px] bg-slate-50"
                    >
                      {factor}
                    </Badge>
                  ))}
                </div>
              </TableCell>
              <TableCell>
                <Badge
                  variant={
                    student.trend === "worsening" ? "destructive" : "secondary"
                  }
                >
                  {student.trend}
                </Badge>
              </TableCell>
              <TableCell className="text-right">
                <DropdownMenu>
                  <DropdownMenuTrigger asChild>
                    <Button variant="ghost" className="h-8 w-8 p-0">
                      <span className="sr-only">Open menu</span>
                      <MoreHorizontal className="h-4 w-4" />
                    </Button>
                  </DropdownMenuTrigger>
                  <DropdownMenuContent align="end">
                    <DropdownMenuLabel>Actions</DropdownMenuLabel>
                    <DropdownMenuItem>View Profile</DropdownMenuItem>
                    <DropdownMenuItem className="text-orange-600">
                      Create Intervention
                    </DropdownMenuItem>
                    <DropdownMenuItem>Contact Parents</DropdownMenuItem>
                  </DropdownMenuContent>
                </DropdownMenu>
              </TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </div>
  );
}

"use client";

import { useState } from "react";
import { Eye, EyeOff, Check, X } from "lucide-react";
import { Input, InputProps } from "@/components/ui/input";
import { cn } from "@/lib/utils";
import { motion, AnimatePresence } from "framer-motion";

interface PasswordInputProps extends InputProps {
  showStrength?: boolean;
}

export function PasswordInput({
  className,
  showStrength = false,
  value,
  ...props
}: PasswordInputProps) {
  const [showPassword, setShowPassword] = useState(false);
  const [strength, setStrength] = useState(0);

  const calculateStrength = (val: string) => {
    let score = 0;
    if (!val) return 0;
    if (val.length > 7) score++;
    if (/[A-Z]/.test(val)) score++;
    if (/[0-9]/.test(val)) score++;
    if (/[^A-Za-z0-9]/.test(val)) score++;
    return score;
  };

  const currentVal = (value as string) || "";

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (showStrength) {
      setStrength(calculateStrength(e.target.value));
    }
    props.onChange?.(e);
  };

  const getStrengthColor = (score: number) => {
    if (score === 0) return "bg-neutral-200";
    if (score <= 1) return "bg-red-500";
    if (score === 2) return "bg-yellow-500";
    if (score === 3) return "bg-blue-500";
    return "bg-green-500";
  };

  const getStrengthText = (score: number) => {
    if (score === 0) return "Enter password";
    if (score <= 1) return "Weak";
    if (score === 2) return "Fair";
    if (score === 3) return "Good";
    return "Strong";
  };

  return (
    <div className="space-y-2">
      <div className="relative">
        <Input
          type={showPassword ? "text" : "password"}
          className={cn("pr-10", className)}
          value={value}
          {...props}
          onChange={handleInputChange}
        />
        <button
          type="button"
          onClick={() => setShowPassword(!showPassword)}
          className="absolute right-3 top-1/2 -translate-y-1/2 text-neutral-400 hover:text-neutral-600 focus:outline-none"
          tabIndex={-1}
        >
          {showPassword ? (
            <EyeOff className="w-4 h-4" />
          ) : (
            <Eye className="w-4 h-4" />
          )}
        </button>
      </div>

      {showStrength && currentVal.length > 0 && (
        <motion.div
          initial={{ opacity: 0, height: 0 }}
          animate={{ opacity: 1, height: "auto" }}
          className="space-y-1"
        >
          <div className="flex gap-1 h-1">
            {[1, 2, 3, 4].map((level) => (
              <div
                key={level}
                className={cn(
                  "h-full flex-1 rounded-full transition-all duration-300",
                  strength >= level
                    ? getStrengthColor(strength)
                    : "bg-neutral-100 dark:bg-neutral-800",
                )}
              />
            ))}
          </div>
          <p
            className={cn(
              "text-xs font-medium text-right",
              strength <= 1
                ? "text-red-500"
                : strength === 2
                  ? "text-yellow-600"
                  : strength === 3
                    ? "text-blue-600"
                    : "text-green-600",
            )}
          >
            {getStrengthText(strength)}
          </p>
        </motion.div>
      )}
    </div>
  );
}

"use client";

import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { motion, AnimatePresence } from "framer-motion";
import { Loader2, ArrowRight, ArrowLeft, CheckCircle2 } from "lucide-react";
import { useRouter } from "next/navigation";
import { toast } from "sonner";

import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { StepIndicator } from "@/components/ui/step-indicator";
import { PasswordInput } from "@/components/auth/password-input";
import { AuthCard } from "@/components/auth/auth-card";
import {
  registerStep1Schema,
  registerStep2Schema,
  registerStep3Schema,
  RegisterStep1Input,
  RegisterStep2Input,
  RegisterStep3Input,
} from "@/lib/validations/auth";
import { createClient } from "@/lib/supabase/client";

const steps = [
  { label: "Personal Info", description: "Your basic details" },
  { label: "Security", description: "Secure your account" },
  { label: "Role & School", description: "Join your institution" },
];

export function RegisterWizard() {
  const [currentStep, setCurrentStep] = useState(1);
  const [direction, setDirection] = useState(0);
  const [isLoading, setIsLoading] = useState(false);
  const router = useRouter();
  const supabase = createClient();

  // Form states for each step
  const step1Form = useForm<RegisterStep1Input>({
    resolver: zodResolver(registerStep1Schema),
    defaultValues: { fullName: "", email: "" },
  });

  const step2Form = useForm<RegisterStep2Input>({
    resolver: zodResolver(registerStep2Schema),
    defaultValues: { password: "", confirmPassword: "" },
  });

  const step3Form = useForm<RegisterStep3Input>({
    resolver: zodResolver(registerStep3Schema),
    defaultValues: { role: undefined, schoolName: "" },
  });

  // Navigation handlers
  const nextStep = () => {
    setDirection(1);
    setCurrentStep((prev) => Math.min(prev + 1, steps.length));
  };

  const prevStep = () => {
    setDirection(-1);
    setCurrentStep((prev) => Math.max(prev - 1, 1));
  };

  const onStep1Submit = async (data: RegisterStep1Input) => {
    // Validate email uniqueness check could go here
    nextStep();
  };

  const onStep2Submit = async (data: RegisterStep2Input) => {
    nextStep();
  };

  const onFinalSubmit = async (data: RegisterStep3Input) => {
    setIsLoading(true);
    try {
      const step1Data = step1Form.getValues();
      const step2Data = step2Form.getValues();

      const { data: authData, error: authError } = await supabase.auth.signUp({
        email: step1Data.email,
        password: step2Data.password,
        options: {
          data: {
            full_name: step1Data.fullName,
            role: data.role,
          },
        },
      });

      if (authError) {
        toast.error(authError.message);
        return;
      }

      toast.success("Account created successfully! Please check your email.");
      router.push("/auth/login?registered=true");
    } catch (error) {
      toast.error("Something went wrong. Please try again.");
    } finally {
      setIsLoading(false);
    }
  };

  // Animation variants
  const variants = {
    enter: (direction: number) => ({
      x: direction > 0 ? 50 : -50,
      opacity: 0,
    }),
    center: {
      zIndex: 1,
      x: 0,
      opacity: 1,
    },
    exit: (direction: number) => ({
      zIndex: 0,
      x: direction < 0 ? 50 : -50,
      opacity: 0,
    }),
  };

  return (
    <div className="w-full max-w-lg mx-auto">
      <StepIndicator
        currentStep={currentStep}
        totalSteps={steps.length}
        steps={steps}
      />

      <AuthCard
        title={
          currentStep === 1
            ? "Create an Account"
            : currentStep === 2
              ? "Secure Account"
              : "Final Details"
        }
        subtitle={
          currentStep === 1
            ? "Enter your details to get started"
            : currentStep === 2
              ? "Choose a strong password"
              : "Tell us about your role"
        }
      >
        <div className="relative overflow-hidden min-h-[300px]">
          <AnimatePresence initial={false} custom={direction} mode="wait">
            {currentStep === 1 && (
              <motion.div
                key="step1"
                custom={direction}
                variants={variants}
                initial="enter"
                animate="center"
                exit="exit"
                transition={{
                  x: { type: "spring", stiffness: 300, damping: 30 },
                  opacity: { duration: 0.2 },
                }}
                className="absolute inset-0"
              >
                <form
                  onSubmit={step1Form.handleSubmit(onStep1Submit)}
                  className="space-y-4"
                >
                  <div className="space-y-2">
                    <Label htmlFor="fullName">Full Name</Label>
                    <Input
                      id="fullName"
                      placeholder="John Doe"
                      {...step1Form.register("fullName")}
                      className={
                        step1Form.formState.errors.fullName
                          ? "border-red-500 focus-visible:ring-red-500"
                          : ""
                      }
                    />
                    {step1Form.formState.errors.fullName && (
                      <p className="text-xs text-red-500">
                        {step1Form.formState.errors.fullName.message}
                      </p>
                    )}
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="email">Email</Label>
                    <Input
                      id="email"
                      type="email"
                      placeholder="john@school.edu"
                      {...step1Form.register("email")}
                      className={
                        step1Form.formState.errors.email
                          ? "border-red-500 focus-visible:ring-red-500"
                          : ""
                      }
                    />
                    {step1Form.formState.errors.email && (
                      <p className="text-xs text-red-500">
                        {step1Form.formState.errors.email.message}
                      </p>
                    )}
                  </div>
                  <div className="pt-4 flex justify-end">
                    <Button type="submit" className="w-full sm:w-auto">
                      Next Step <ArrowRight className="w-4 h-4 ml-2" />
                    </Button>
                  </div>
                </form>
              </motion.div>
            )}

            {currentStep === 2 && (
              <motion.div
                key="step2"
                custom={direction}
                variants={variants}
                initial="enter"
                animate="center"
                exit="exit"
                transition={{
                  x: { type: "spring", stiffness: 300, damping: 30 },
                  opacity: { duration: 0.2 },
                }}
                className="absolute inset-0"
              >
                <form
                  onSubmit={step2Form.handleSubmit(onStep2Submit)}
                  className="space-y-4"
                >
                  <div className="space-y-2">
                    <Label htmlFor="password">Password</Label>
                    <PasswordInput
                      id="password"
                      placeholder="â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢"
                      showStrength
                      {...step2Form.register("password")}
                      className={
                        step2Form.formState.errors.password
                          ? "border-red-500 focus-visible:ring-red-500"
                          : ""
                      }
                    />
                    {step2Form.formState.errors.password && (
                      <p className="text-xs text-red-500">
                        {step2Form.formState.errors.password.message}
                      </p>
                    )}
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="confirmPassword">Confirm Password</Label>
                    <PasswordInput
                      id="confirmPassword"
                      placeholder="â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢"
                      {...step2Form.register("confirmPassword")}
                      className={
                        step2Form.formState.errors.confirmPassword
                          ? "border-red-500 focus-visible:ring-red-500"
                          : ""
                      }
                    />
                    {step2Form.formState.errors.confirmPassword && (
                      <p className="text-xs text-red-500">
                        {step2Form.formState.errors.confirmPassword.message}
                      </p>
                    )}
                  </div>
                  <div className="pt-4 flex justify-between">
                    <Button
                      type="button"
                      variant="outline"
                      onClick={prevStep}
                      className="w-[48%]"
                    >
                      <ArrowLeft className="w-4 h-4 mr-2" /> Back
                    </Button>
                    <Button type="submit" className="w-[48%]">
                      Next Step <ArrowRight className="w-4 h-4 ml-2" />
                    </Button>
                  </div>
                </form>
              </motion.div>
            )}

            {currentStep === 3 && (
              <motion.div
                key="step3"
                custom={direction}
                variants={variants}
                initial="enter"
                animate="center"
                exit="exit"
                transition={{
                  x: { type: "spring", stiffness: 300, damping: 30 },
                  opacity: { duration: 0.2 },
                }}
                className="absolute inset-0"
              >
                <form
                  onSubmit={step3Form.handleSubmit(onFinalSubmit)}
                  className="space-y-4"
                >
                  <div className="space-y-2">
                    <Label htmlFor="role">I am a...</Label>
                    <Select
                      onValueChange={(val) =>
                        step3Form.setValue("role", val as any)
                      }
                      defaultValue={step3Form.getValues("role")}
                    >
                      <SelectTrigger>
                        <SelectValue placeholder="Select your role" />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="admin">Administrator</SelectItem>
                        <SelectItem value="teacher">Teacher</SelectItem>
                        <SelectItem value="student">Student</SelectItem>
                        <SelectItem value="parent">Parent</SelectItem>
                      </SelectContent>
                    </Select>
                    {step3Form.formState.errors.role && (
                      <p className="text-xs text-red-500">
                        {step3Form.formState.errors.role.message}
                      </p>
                    )}
                  </div>

                  {step3Form.watch("role") !== "parent" && ( // Example condition
                    <div className="space-y-2">
                      <Label htmlFor="schoolName">School Name (Optional)</Label>
                      <Input
                        id="schoolName"
                        placeholder="e.g. Springfield High"
                        {...step3Form.register("schoolName")}
                      />
                    </div>
                  )}

                  <div className="pt-4 flex justify-between">
                    <Button
                      type="button"
                      variant="outline"
                      onClick={prevStep}
                      className="w-[48%]"
                    >
                      <ArrowLeft className="w-4 h-4 mr-2" /> Back
                    </Button>
                    <Button
                      type="submit"
                      disabled={isLoading}
                      className="w-[48%] bg-brand-600 hover:bg-brand-700 text-white"
                    >
                      {isLoading ? (
                        <>
                          <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                          Creating...
                        </>
                      ) : (
                        <>
                          Create Account{" "}
                          <CheckCircle2 className="w-4 h-4 ml-2" />
                        </>
                      )}
                    </Button>
                  </div>
                </form>
              </motion.div>
            )}
          </AnimatePresence>
        </div>
      </AuthCard>
    </div>
  );
}

"use client";

import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { motion, AnimatePresence } from "framer-motion";
import {
  Loader2,
  ArrowRight,
  ArrowLeft,
  CheckCircle2,
  Building2,
  MapPin,
  UserCircle,
  Settings as SettingsIcon,
  FileCheck,
  Sparkles,
} from "lucide-react";
import { useRouter } from "next/navigation";
import { toast } from "sonner";

import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Checkbox } from "@/components/ui/checkbox";
import { AuthCard } from "@/components/auth/auth-card";
import {
  schoolInfoSchema,
  locationSchema,
  adminAccountSchema,
  preferencesSchema,
  reviewSchema,
  SchoolInfoInput,
  LocationInput,
  AdminAccountInput,
  PreferencesInput,
  ReviewInput,
  CompleteRegistrationData,
} from "@/lib/validations/school-registration";
import { PasswordInput } from "@/components/auth/password-input";

const steps = [
  {
    label: "School Info",
    description: "Basic details",
    icon: Building2,
  },
  {
    label: "Location",
    description: "Address & contact",
    icon: MapPin,
  },
  {
    label: "Admin Account",
    description: "Your credentials",
    icon: UserCircle,
  },
  {
    label: "Preferences",
    description: "Optional settings",
    icon: SettingsIcon,
  },
  {
    label: "Review",
    description: "Confirm & submit",
    icon: FileCheck,
  },
];

export function SchoolRegistrationWizard() {
  const [currentStep, setCurrentStep] = useState(1);
  const [direction, setDirection] = useState(0);
  const [isLoading, setIsLoading] = useState(false);
  const router = useRouter();

  // Form states for each step
  const step1Form = useForm<SchoolInfoInput>({
    resolver: zodResolver(schoolInfoSchema),
    defaultValues: {
      schoolName: "",
      website: "",
      schoolType: undefined,
      studentCountRange: undefined,
    },
  });

  const step2Form = useForm<LocationInput>({
    resolver: zodResolver(locationSchema),
    defaultValues: {
      addressLine1: "",
      addressLine2: "",
      city: "",
      state: "",
      postalCode: "",
      country: "India",
      phone: "",
    },
  });

  const step3Form = useForm<AdminAccountInput>({
    resolver: zodResolver(adminAccountSchema),
    defaultValues: {
      adminFullName: "",
      adminEmail: "",
      adminPhone: "",
      password: "",
      confirmPassword: "",
    },
  });

  const step4Form = useForm<PreferencesInput>({
    resolver: zodResolver(preferencesSchema),
    defaultValues: {
      academicYearStartMonth: undefined,
      primaryLanguage: "English",
      timezone: "Asia/Kolkata",
    },
  });

  const step5Form = useForm<ReviewInput>({
    resolver: zodResolver(reviewSchema),
    defaultValues: { acceptTerms: false },
  });

  // Navigation
  const nextStep = () => {
    setDirection(1);
    setCurrentStep((prev) => Math.min(prev + 1, steps.length));
  };

  const prevStep = () => {
    setDirection(-1);
    setCurrentStep((prev) => Math.max(prev - 1, 1));
  };

  // Step handlers
  const onStep1Submit = async (data: SchoolInfoInput) => {
    nextStep();
  };

  const onStep2Submit = async (data: LocationInput) => {
    nextStep();
  };

  const onStep3Submit = async (data: AdminAccountInput) => {
    nextStep();
  };

  const onStep4Submit = async (data: PreferencesInput) => {
    nextStep();
  };

  const onFinalSubmit = async (data: ReviewInput) => {
    setIsLoading(true);
    try {
      // Combine all form data
      const completeData: CompleteRegistrationData = {
        ...step1Form.getValues(),
        ...step2Form.getValues(),
        ...step3Form.getValues(),
        ...step4Form.getValues(),
        ...data,
      };

      const response = await fetch("/api/register-school", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(completeData),
      });

      const result = await response.json();

      if (!response.ok) {
        toast.error(result.error || "Registration failed");
        return;
      }

      toast.success("ðŸŽ‰ School registered successfully!");
      router.push("/auth/login?registered=true");
    } catch (error) {
      console.error("Registration error:", error);
      toast.error("Something went wrong. Please try again.");
    } finally {
      setIsLoading(false);
    }
  };

  // Animation variants
  const variants = {
    enter: (direction: number) => ({
      x: direction > 0 ? 50 : -50,
      opacity: 0,
    }),
    center: {
      zIndex: 1,
      x: 0,
      opacity: 1,
    },
    exit: (direction: number) => ({
      zIndex: 0,
      x: direction < 0 ? 50 : -50,
      opacity: 0,
    }),
  };

  // Progress percentage
  const progress = (currentStep / steps.length) * 100;

  return (
    <div className="w-full max-w-3xl mx-auto">
      {/* Progress Bar */}
      <div className="mb-8">
        <div className="flex justify-between items-center mb-4">
          <div className="flex items-center gap-2">
            <Sparkles className="w-5 h-5 text-brand-600" />
            <h2 className="text-lg font-semibold text-neutral-900 dark:text-white">
              School Registration
            </h2>
          </div>
          <span className="text-sm font-medium text-neutral-600 dark:text-neutral-400">
            Step {currentStep} of {steps.length}
          </span>
        </div>

        {/* Progress bar */}
        <div className="relative h-2 bg-neutral-200 dark:bg-neutral-800 rounded-full overflow-hidden">
          <motion.div
            className="absolute inset-y-0 left-0 bg-gradient-to-r from-brand-600 to-brand-500"
            initial={{ width: "0%" }}
            animate={{ width: `${progress}%` }}
            transition={{ duration: 0.3 }}
          />
        </div>

        {/* Step indicators */}
        <div className="flex justify-between mt-4">
          {steps.map((step, index) => {
            const stepNum = index + 1;
            const isActive = stepNum === currentStep;
            const isCompleted = stepNum < currentStep;
            const Icon = step.icon;

            return (
              <div
                key={stepNum}
                className="flex flex-col items-center gap-2 flex-1"
              >
                <div
                  className={`w-10 h-10 rounded-full flex items-center justify-center transition-all ${
                    isCompleted
                      ? "bg-brand-600 text-white"
                      : isActive
                        ? "bg-brand-100 dark:bg-brand-900 text-brand-700 dark:text-brand-400 ring-2 ring-brand-600"
                        : "bg-neutral-100 dark:bg-neutral-800 text-neutral-400"
                  }`}
                >
                  {isCompleted ? (
                    <CheckCircle2 className="w-5 h-5" />
                  ) : (
                    <Icon className="w-5 h-5" />
                  )}
                </div>
                <div className="hidden sm:flex flex-col items-center">
                  <span
                    className={`text-xs font-medium ${
                      isActive || isCompleted
                        ? "text-neutral-900 dark:text-white"
                        : "text-neutral-500"
                    }`}
                  >
                    {step.label}
                  </span>
                  <span className="text-[10px] text-neutral-400">
                    {step.description}
                  </span>
                </div>
              </div>
            );
          })}
        </div>
      </div>

      <AuthCard
        title={steps[currentStep - 1].label}
        subtitle={`Step ${currentStep} of ${steps.length}: ${steps[currentStep - 1].description}`}
      >
        <div className="relative overflow-hidden min-h-[400px]">
          <AnimatePresence initial={false} custom={direction} mode="wait">
            {/* Step 1: School Info */}
            {currentStep === 1 && (
              <motion.div
                key="step1"
                custom={direction}
                variants={variants}
                initial="enter"
                animate="center"
                exit="exit"
                transition={{
                  x: { type: "spring", stiffness: 300, damping: 30 },
                  opacity: { duration: 0.2 },
                }}
                className="absolute inset-0"
              >
                <form
                  onSubmit={step1Form.handleSubmit(onStep1Submit)}
                  className="space-y-4"
                >
                  <div className="space-y-2">
                    <Label htmlFor="schoolName">
                      School Name <span className="text-red-500">*</span>
                    </Label>
                    <Input
                      id="schoolName"
                      placeholder="e.g., Springfield High School"
                      {...step1Form.register("schoolName")}
                      className={
                        step1Form.formState.errors.schoolName
                          ? "border-red-500"
                          : ""
                      }
                    />
                    {step1Form.formState.errors.schoolName && (
                      <p className="text-xs text-red-500">
                        {step1Form.formState.errors.schoolName.message}
                      </p>
                    )}
                  </div>

                  <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
                    <div className="space-y-2">
                      <Label htmlFor="schoolType">
                        School Type <span className="text-red-500">*</span>
                      </Label>
                      <Select
                        onValueChange={(val) =>
                          step1Form.setValue("schoolType", val as any)
                        }
                        defaultValue={step1Form.getValues("schoolType")}
                      >
                        <SelectTrigger>
                          <SelectValue placeholder="Select type" />
                        </SelectTrigger>
                        <SelectContent>
                          <SelectItem value="k12">K-12 School</SelectItem>
                          <SelectItem value="higher_ed">
                            Higher Education
                          </SelectItem>
                          <SelectItem value="vocational">
                            Vocational/Technical
                          </SelectItem>
                          <SelectItem value="other">Other</SelectItem>
                        </SelectContent>
                      </Select>
                      {step1Form.formState.errors.schoolType && (
                        <p className="text-xs text-red-500">
                          {step1Form.formState.errors.schoolType.message}
                        </p>
                      )}
                    </div>

                    <div className="space-y-2">
                      <Label htmlFor="studentCountRange">
                        Student Count <span className="text-red-500">*</span>
                      </Label>
                      <Select
                        onValueChange={(val) =>
                          step1Form.setValue("studentCountRange", val as any)
                        }
                        defaultValue={step1Form.getValues("studentCountRange")}
                      >
                        <SelectTrigger>
                          <SelectValue placeholder="Approx. students" />
                        </SelectTrigger>
                        <SelectContent>
                          <SelectItem value="1-100">1-100</SelectItem>
                          <SelectItem value="101-500">101-500</SelectItem>
                          <SelectItem value="501-1000">501-1000</SelectItem>
                          <SelectItem value="1000+">1000+</SelectItem>
                        </SelectContent>
                      </Select>
                      {step1Form.formState.errors.studentCountRange && (
                        <p className="text-xs text-red-500">
                          {step1Form.formState.errors.studentCountRange.message}
                        </p>
                      )}
                    </div>
                  </div>

                  <div className="space-y-2">
                    <Label htmlFor="website">Website (Optional)</Label>
                    <Input
                      id="website"
                      type="url"
                      placeholder="https://yourschool.edu"
                      {...step1Form.register("website")}
                      className={
                        step1Form.formState.errors.website
                          ? "border-red-500"
                          : ""
                      }
                    />
                    {step1Form.formState.errors.website && (
                      <p className="text-xs text-red-500">
                        {step1Form.formState.errors.website.message}
                      </p>
                    )}
                  </div>

                  <div className="pt-4 flex justify-end">
                    <Button type="submit" className="w-full sm:w-auto">
                      Next Step <ArrowRight className="w-4 h-4 ml-2" />
                    </Button>
                  </div>
                </form>
              </motion.div>
            )}

            {/* Step 2: Location */}
            {currentStep === 2 && (
              <motion.div
                key="step2"
                custom={direction}
                variants={variants}
                initial="enter"
                animate="center"
                exit="exit"
                transition={{
                  x: { type: "spring", stiffness: 300, damping: 30 },
                  opacity: { duration: 0.2 },
                }}
                className="absolute inset-0"
              >
                <form
                  onSubmit={step2Form.handleSubmit(onStep2Submit)}
                  className="space-y-4"
                >
                  <div className="space-y-2">
                    <Label htmlFor="addressLine1">
                      Address Line 1 <span className="text-red-500">*</span>
                    </Label>
                    <Input
                      id="addressLine1"
                      placeholder="Street address"
                      {...step2Form.register("addressLine1")}
                      className={
                        step2Form.formState.errors.addressLine1
                          ? "border-red-500"
                          : ""
                      }
                    />
                    {step2Form.formState.errors.addressLine1 && (
                      <p className="text-xs text-red-500">
                        {step2Form.formState.errors.addressLine1.message}
                      </p>
                    )}
                  </div>

                  <div className="space-y-2">
                    <Label htmlFor="addressLine2">Address Line 2</Label>
                    <Input
                      id="addressLine2"
                      placeholder="Apartment, suite, etc. (optional)"
                      {...step2Form.register("addressLine2")}
                    />
                  </div>

                  <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
                    <div className="space-y-2">
                      <Label htmlFor="city">
                        City <span className="text-red-500">*</span>
                      </Label>
                      <Input
                        id="city"
                        placeholder="City"
